mapper.select:
select id, uid, from_scores as fromScores, scores, order_no as orderNo, change_scores as changeScores, type, src_id as srcId, comment, DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s') as createTime from ${table} where uid=#{uid}
and id = #{id}
and order_no = #{order_no}
and src_id = #{src_id}
order by create_time desc

select uid
from t_user_scores

    1=1

  and uid = #{uid}


    and create_time >= #{start_time}


    and create_time <= #{end_time}



    order by create_time desc


    order by scores desc


    order by scores asc
